# Depth is how far the script will go to pull directory permission in the event of groups added deeper into the directory tree that need to be audited.

# Array of directories you want to audit and report on
$DirectoriesToAudit = @(
    @{
        Name = "";
        Directory = "";
        DirectorEmail = "";
        Depth = 0;
        ExcelFilePath = "";
    }
    @{
        Name = "";
        Directory = "";
        DirectorEmail = "";
        Depth = 0;
        ExcelFilePath = "";
    }
    @{
        Name = "";
        Directory = "";
        DirectorEmail = "";
        Depth = 0;
        ExcelFilePath = "";
    }
);

ForEach ($DirectoryToAudit in $DirectoriesToAudit) {

    $TargetDirectory = $DirectoryToAudit.Directory;
    $Depth = $DirectoryToAudit.Depth;
    $ExcelFilePath = $DirectoryToAudit.ExcelFilePath

    $EmailParams = @{
        From         = "";
        To           = $DirectoryToAudit.DirectorEmail;
        CC           = "";
        Subject      = "$($DirectoryToAudit.Name) Directory Audit";
        Body         = "";
        SMTPServer   = "";
        Port         = "25";
        Attachment   = "";
    };

    $Directories = Get-ChildItem -Path $TargetDirectory -Recurse -Depth $Depth -Directory -Force -ErrorAction SilentlyContinue;

    $Date = (((Get-Date).ToUniversalTime()).ToString("ddMMMyyyy"))

    $ExcelFile = "$($ExcelFilePath)\$(($DirectoryToAudit.Name -replace ' ',''))PermsAudit_$($Date).xlsx";

    ForEach ($Directory in $Directories) {

        $Groups = (Get-Acl -Path $Directory.FullName).Access | 
            Where-Object -FilterScript { $_.IdentityReference -ne 'FSU\gg-Isilon-FS-Admins' } |
            Where-Object -FilterScript { $_.IdentityReference -ne 'FSU\CCCJ-FS-DONOTREMOVE' } |
            Where-Object -FilterScript { $_.IdentityReference -ne 'NT AUTHORITY\SYSTEM' };

        $Properties = @(
            "DisplayName";
            "SamAccountName";
            "Emailaddress";
        );

        $DataSelect = @(
            @{n="Name";e={$_."DisplayName"}};
            @{n="Permissions";e={$_."Permissions"}};
            @{n="Group";e={$_."GroupName"}};
            @{n="FSUID";e={$_."SamAccountName"}};
        );

        $Users = @();

        ForEach ($Group in $Groups) {
        
            $GroupName = $Group.IdentityReference.Value.trimstart('FSU\');
            $Permissions = $Group.FileSystemRights;
            $Permissions = $Permissions -replace ", Synchronize","";
        
            $GroupUsers = Get-ADGroupMember $Group.IdentityReference.Value.trimstart('FSU\') | 
                Where {$_.objectclass -eq 'user'} | 
                Get-ADUser -Properties $Properties;
        
            $MemberGroups = Get-ADGroupMember $Group.IdentityReference.Value.trimstart('FSU\') | 
                Where {$_.objectclass -eq 'group'} | Where-Object -FilterScript { $_.name -ne 'gg-Isilon-FS-Admins' } | 
                Where-Object -FilterScript { $_.name -ne 'FSU\CCCJ-FS-DONOTREMOVE' };

            $GroupUsers | Add-Member NoteProperty GroupName($fiel.GroupName) -ErrorAction SilentlyContinue;
            $GroupUsers | Add-Member NoteProperty Permissions($fiel.Permissions) -ErrorAction SilentlyContinue;

            ForEach ($GroupUser in $GroupUsers) {
                $GroupUser.GroupName = $GroupName;
                $GroupUser.Permissions = $Permissions;
            }

            $Users += $GroupUsers;

            ForEach ($MemberGroup in $MemberGroups) {
            
                $MemberGroupUsers = Get-ADGroupMember $MemberGroup.name | 
                    Where {$_.objectclass -eq 'user'} |
                    Get-ADUser -Properties $Properties;
            
                $MemberGroupUsers | Add-Member NoteProperty GroupName($fiel.GroupName) -ErrorAction SilentlyContinue;
                $MemberGroupUsers | Add-Member NoteProperty Permissions($fiel.Permissions) -ErrorAction SilentlyContinue;

                ForEach ($MemberGroupUser in $MemberGroupUsers) {
                    $MemberGroupUser.GroupName = $GroupName;
                    $MemberGroupUser.Permissions = $Permissions;
                }
           
                $Users += $MemberGroupUsers;
            }
        }
        $Users = $Users | Select $DataSelect -Unique | Sort-Object Name;
        $Users | Export-Excel -Path $ExcelFile -WorksheetName $Directory.Name -Title $Directory.FullName -TitleBold -TitleSize 13 -AutoFilter -AutoSize;
    }

    $EmailParams.Body = @"
<!DOCTYPE html>
<html>
    <head>
        <meta http-equiv="Content-Type" content="text/html; charset=utf-8"><meta name="viewport" content="width=device-width, initial-scale=1.0">
    </head>
    <body style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; background-color: #f8fafc; color: #74787e; height: 100%; hyphens: auto; line-height: 1.4; margin: 0; -moz-hyphens: auto; -ms-word-break: break-all; width: 100% !important; -webkit-hyphens: auto; -webkit-text-size-adjust: none; word-break: break-word;">
        <style>
            @media  only screen and (max-width: 600px) {
                .inner-body {
                    width: 100% !important;
                }

                .footer {
                    width: 100% !important;
                }
            }

            @media  only screen and (max-width: 500px) {
                .button {
                    width: 100% !important;
                }
            }
            .center {
                  display: block;
                  margin-left: auto;
                  margin-right: auto;
                  width: 50%;
            }
        </style>

        <table class="wrapper" width="100%" cellpadding="0" cellspacing="0" role="presentation" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; background-color: #f8fafc; margin: 0; padding: 0; width: 100%; -premailer-cellpadding: 0; -premailer-cellspacing: 0; -premailer-width: 100%;">
            <tr>
                <td align="center" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box;">
                    <table class="content" width="100%" cellpadding="0" cellspacing="0" role="presentation" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; margin: 0; padding: 0; width: 100%; -premailer-cellpadding: 0; -premailer-cellspacing: 0; -premailer-width: 100%;">
                        <tr>
                            <td class="header" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; padding: 25px 0; text-align: center; vertical-align: middle;">
                                <img src="data:image/png;base64,">
                                <p style="text-align:center; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; color: #2b2d30; font-size: 25px; font-weight: bold; text-decoration: none; text-shadow: 0 1px 0 white; vertical-align: middle;">
                                    File Server Permissions Auditor
                                </p>
                            </td>
                        </tr>
                        <tr>
                            <td class="body" width="100%" cellpadding="0" cellspacing="0" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; background-color: #ffffff; border-bottom: 1px solid #edeff2; border-top: 1px solid #edeff2; margin: 0; padding: 0; width: 100%; -premailer-cellpadding: 0; -premailer-cellspacing: 0; -premailer-width: 100%; vertical-align: middle;">
                                <table class="inner-body" align="center" width="570" cellpadding="0" cellspacing="0" role="presentation" style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; background-color: #ffffff; margin: 0 auto; padding: 0; width: 90%; -premailer-cellpadding: 0; -premailer-cellspacing: 0; -premailer-width: 570px;">
                                    <!-- Body content -->
                                    <tr>
                                        <td class="content-cell" style="text-align:center; font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; padding: 35px;">
                                            <p style="font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Helvetica, Arial, sans-serif, 'Apple Color Emoji', 'Segoe UI Emoji', 'Segoe UI Symbol'; box-sizing: border-box; color: #3d4852; font-size: 16px; line-height: 1.5em; margin-top: 0; text-align: left;">
                                                Good Morning!
                                                <br>
                                                <br>
                                                Attached are the current File Server permissions for:<br>
                                                $($TargetDirectory)
                                                <br>
                                                Please review the attach spreadsheet and reply with any necessary corrections.
                                                <br>
                                                <br><b>
                                                NOTE: Each file server folder is a sheet in the excel 
                                                workbook. You will need to use the arrow keys in excel 
                                                to navigate to each sheet in the workbook.
                                                </b><br>
                                                <br>
                                                Best Regards,<br>
                                                CCCJ-IT
                                            </p>
                                        </td>
                                    </tr>
                                </table>
                            </td>
                        </tr>
                    </table>
                </td>
            </tr>
        </table>
    </body>
</html>
"@;

    $EmailParams.Attachment = $ExcelFile;
    Send-MailMessage @EmailParams -BodyAsHTML;

}
